import React, {useState, useEffect} from 'react'
import Button from 'react-bootstrap/Button'
import { CONSTANTS } from '../../constants'
import md5 from 'md5'


export default function Register(props){
    const [userName, setUserName] = useState("")
    const [password, setPassword] = useState("")
    const [rePassword, setRePassword] = useState("")
    const [variant, setVariant] = useState("")
    const [status, setStatus] = useState(false)
    const [message, setMessage] = useState("")

    const handleAlertClose = ()=>{
        setMessage("")
        setVariant("")
        setStatus(false)
    }
    const onInputChange = (e)=>{
        const name = e.target.name
        handleAlertClose()
        switch(name){
            case "username":
                setUserName(e.target.value)
                break
            case "password":
                setPassword(e.target.value)
                break
            case "re-password":
                setRePassword(e.target.value)
            default:
                console.log("error")
        }
    }
    const handleRegister = async ()=>{
        if(userName === ""){
            setStatus(true)
            setVariant("danger")
            setMessage("Please enter username to continue")
            return
        }
        if(password === ""){
            setStatus(true)
            setVariant("danger")
            setMessage("Please enter password to continue")
            return
        }
        if(rePassword === ""){
            setStatus(true)
            setVariant("danger")
            setMessage("Please re-enter password to continue")
            return
        }
        if(password !== rePassword){
            setStatus(true)
            setVariant("danger")
            setMessage("Password and duplicate password must match")
            return
        }
        const loginObject = {
            username : userName,
            password : md5(password)
        }

        const response = await fetch(`${CONSTANTS.auth_url}/register`,{
            method : "POST",
            headers : {"Content-type" : "application/json"},
            body : JSON.stringify(loginObject)
        })

        const data = await response.json()
        if(data.status){
            console.log('register-success')
        }
        else{
            setStatus(true)
            setVariant("danger")
            setMessage(data.error)
        }

    }
    return <div>
        <div className='row d-flex justify-content-center'>
            <div className='col-lg-4 col-md-6 col-sm-12'>
                <div className='form m-3'>
                    <div className='row mb-1'>
                        <Alert key={variant} variant={variant} show={status} dismissible onClose={handleAlertClose}>
                            {message}
                        </Alert>
                    </div>
                    <div class="row mb-2">
                        <label className='form-label p-0' htmlFor='username'>Username</label>
                        <input type="text" name="username" id="username" className='form-control' value={userName} placeholder="Enter Username" onChange={onInputChange}/>
                    </div>
                    <div class="row mb-2">
                        <label className='form-label p-0' htmlFor='password'>Password</label>
                        <input type="text" name="password" id="password" className='form-control' value={password} placeholder="Enter Password" onChange={onInputChange}/>
                    </div>
                    <div class="row mb-2">
                        <label className='form-label p-0' htmlFor='password'>Re-enter Password</label>
                        <input type="text" name="re-password" id="re-password" className='form-control' value={rePassword} placeholder="Verify Password" onChange={onInputChange}/>
                    </div>
                    <Button variant="primary" onClick={handleRegister} type="button">Register</Button>
                </div>
            </div>
        </div>
    </div>
}
